function createList(t){var a=t,i="";$.each(a,(function(t,a){i+='<a href="',i+=a.link,i+='" target="_blank"><div class="portfolio-grid__thumbnail';for(var n=0;n<a.tags.length;n++)i+=" "+a.tags[n];i+='"><img src="img/',i+=a.thumbnail,i+='" alt=""></div></a>'})),$(".portfolio-grid").html(i)}$((function(){$.ajax({type:"GET",url:"./portfolio.json",async:!1,beforeSend:function(){},dataType:"json",success:function(t){createList(t),filterSelection("all")}})}));