function createList(t){var a=t,n="";$.each(a,(function(t,a){n+='<a href="',n+=a.link,n+='" target="_blank" class="portfolio-grid__thumbnail';for(var i=0;i<a.tags.length;i++)n+=" "+a.tags[i];n+='"><img src="img/',n+=a.thumbnail,n+='" alt=""></a>'})),$(".portfolio-grid").html(n)}$((function(){$.ajax({type:"GET",url:"./portfolio.json",async:!1,beforeSend:function(){},dataType:"json",success:function(t){createList(t),filterSelection("all")}})}));